function MenuBG(){document.body.scrollTop>10||document.documentElement.scrollTop>10?document.getElementById("top-nav").classList.add("menu-bg"):menu_toggle<0&&document.getElementById("top-nav").classList.remove("menu-bg")}function toggleDisplay(){menu_toggle*=-1;for(var e=document.getElementById("top-nav").getElementsByTagName("div"),t=1;t<e.length;t++)e[t].classList.toggle("displayed");document.body.scrollTop<10&&document.documentElement.scrollTop<10&&document.getElementById("top-nav").classList.toggle("menu-bg")}function toggleSubmenu(e){e.nextElementSibling.classList.toggle("displayed")}window.addEventListener("scroll",MenuBG);var menu_toggle=-1;